import React, {Component} from "react";
import Form from "./Fhonebook/PhonebookForm";
import css from './Fhonebook/fhonebook.module.css';
import { nanoid } from "nanoid";



class App extends Component {
  state = {
    contacts: [],
    name: ''
  }

    addContact = (nameData) => {
    const newContact = {
      id: nanoid(),
      ...nameData,
    }
    this.setState(prevState => ({contacts: [newContact, ...prevState.contacts]}))
  }

  render () {
return (
  <div>
  <Form addContactProps={this.addContact}/>
  </div>
)
}
}

export default App;
