import React, {Component} from "react";

import css from './fhonebook.module.css';
import { nanoid } from "nanoid";
import { ArrayType } from "proptype";


class Form extends Component {
    state = {
        contacts: [],
        name: ''
    }

    handleNameChange = event => {
    // console.log(event.currentTarget.value)
    this.setState({name: event.currentTarget.value})
  }

    hendleSubmit = event => {
    event.preventDefault()
    console.log(this.state)
  }     
//   addName = (contacts) => {
//     this.setState(prevState => ({
//         name: prevState.name.filter(name => contacts !== contacts)
//     }))
//   }


    render () {
        return (
            <div className={css.container}>
    <div className="section">
      <h2 className={css.title}>Phonebook</h2>
      <form className={css.form} onSubmit={this.hendleSubmit}>
      <label className={css.inputName}>Name
      <input
        className={css.input}
        type="text"
        value={this.state.name}
        onChange={this.handleNameChange}
        name="name"
        pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
        title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
        required
      />
      </label> 
      <button type="submit" className={css.button} >Add contact</button>
      </form> 
    </div>
    <div className="section">
      <h2 className={css.title}>Contacts</h2>
      <ul className="contact__item">
        <li className="contactName"></li>
      </ul>
    </div>
  </div>
        );
    }
} 

export default Form;
