import React, {Component} from "react";
import Form from "./Fhonebook/PhonebookForm";
import Contacts from "./Fhonebook/Contacts";
import css from './Fhonebook/fhonebook.module.css';
import { nanoid } from "nanoid";



class App extends Component {
  state = {
    contacts: [
    {id: 'id-1', name: 'Rosie Simpson', number: '459-12-56'},
    {id: 'id-2', name: 'Hermione Kline', number: '443-89-12'},
    {id: 'id-3', name: 'Eden Clements', number: '645-17-79'},
    {id: 'id-4', name: 'Annie Copeland', number: '227-91-26'},
    ],
    filter: '',
    name: '',
    number: '',
  };

  addContact = nameData => {
    const newContact = {
      id: nanoid(),
      ...nameData,
    };
    this.setState(prevState => ({
      contacts: [newContact, ...prevState.contacts],
    }));
  };

  changeFilter = event => {
    this.setState({filter: evecurrentTarget.va})
  }

  render() {
    return (
      <div className={css.container}>
        <h2 className={css.title}>Phonebook</h2>
        <Form addContactProps={this.addContact} />
        <h2 className={css.title}>Contacts</h2>
        <Contacts contacts={this.state.contacts} />
        <h3 className={css.inputName}>Find contacts by name</h3>
        <input
              className={css.input}
              type="text"
              value={this.state.filter}
              name="name"
              pattern="^[a-zA-Zа-яА-Я]+(([' -][a-zA-Zа-яА-Я ])?[a-zA-Zа-яА-Я]*)*$"
              title="Name may contain only letters, apostrophe, dash and spaces. For example Adrian, Jacob Mercer, Charles de Batz de Castelmore d'Artagnan"
              required
            />
      </div>
    );
  }
}

export default App;
